// -------------------------------// adapted from Kevin T. Manley// CSE 593// -------------------------------package ResImpl;public class Hotel extends ReservableItem implements Cloneable{		public Hotel( String location, int count, int price )		{			super( location, count, price );		}		public Object clone() {    		Hotel copy = null;    		try {    			copy = (Hotel)super.clone();    		} catch (CloneNotSupportedException e) {    			e.printStackTrace();    		}    		return copy;    	}		public String getKey()		{			return Hotel.getKey( getLocation() );		}		public static String getKey( String location )		{			String s = "room-" + location  ;			return s.toLowerCase();		}}